import React, { useState, useEffect } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";

function BotsPage() {
  //start here with your code for step one

    const [bots, setBots] = useState([]);
    const [enlistedBots, setEnlistedBots] = useState([]);
    const url = "http://localhost:8002/bots";

    useEffect(() => {
      // Fetching bots data from the backend
      fetch(url)
        .then((response) => response.json())
        .then((data) => setBots(data))
        .catch((error) => console.log(error));
    }, []);

    const enlistBot = (selectedBot) => {
      // Check if the bot is already enlisted
      if (enlistedBots.includes(selectedBot)) {
        return; // Exit if the bot is already enlisted
      }
      // Add the selectedBot to the enlistedBots state
      setEnlistedBots([...enlistedBots, selectedBot]);
    };

      const releaseBot = (releasedBot) => {
        setEnlistedBots(
          enlistedBots.filter((bot) => bot.id !== releasedBot.id)
        );
      };


  return (
    <div>
      <YourBotArmy enlistedBots={enlistedBots} releaseBot={releaseBot} />
      <BotCollection bots={bots} enlistBot={enlistBot} />
    </div>
  );
}

export default BotsPage;
