import React from "react";

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};
// ...

function BotCard({ bot, enlistedBots, setEnlistedBots, deleteBot }) {
  const handleCardClick = () => {
    if (enlistedBots.includes(bot)) {
      // Bot is already enlisted, remove it from the enlistedBots state
      setEnlistedBots(enlistedBots.filter((enlistedBot) => enlistedBot !== bot));
    } else {
      // Bot is not enlisted, enlist it by adding to the enlistedBots state
      setEnlistedBots([...enlistedBots, bot]);
    }
  };

  const handleDeleteClick = (event) => {
    event.stopPropagation(); // Prevent the card click event from firing
    // Call the deleteBot function passed as prop
    deleteBot(bot.id);
    console.log("Delete bot:", bot.id);
  };

  const releaseBot = () => {
    // Release the bot from the army by removing it from the enlistedBots state
    setEnlistedBots(enlistedBots.filter((enlistedBot) => enlistedBot !== bot));
  };

  return (
    <div className="ui column">
      <div className="ui card" key={bot.id} onClick={handleCardClick}>
        <div className="image">
          <img alt="oh no!" src={bot.avatar_url} />
        </div>
        <div className="content">
          <div className="header">
            {bot.name}
            <i className={botTypeClasses[bot.bot_class]} />
          </div>
          <div className="meta text-wrap">
            <small>{bot.catchphrase}</small>
          </div>
        </div>
        <div className="extra content">
          <span>
            <i className="icon heartbeat" />
            {bot.health}
          </span>
          <span>
            <i className="icon lightning" />
            {bot.damage}
          </span>
          <span>
            <i className="icon shield" />
            {bot.armor}
          </span>
          <span>
            <div className="ui center aligned segment basic">
              <button className="ui mini red button" onClick={handleDeleteClick}>
                x
              </button>
              {enlistedBots.includes(bot) && (
                <button className="ui mini blue button" onClick={releaseBot}>
                  Release
                </button>
              )}
            </div>
          </span>
        </div>
      </div>
    </div>
  );
}

export default B
