import React, { useState, useEffect } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";

function BotsPage() {
  const [bots, setBots] = useState([]);
  const [enlistedBots, setEnlistedBots] = useState([]);
  const url = "http://localhost:8002/bots";

  useEffect(() => {
    fetch(url)
      .then((response) => response.json())
      .then((data) => setBots(data))
      .catch((error) => console.log(error));
  }, []);

  const enlistBot = (selectedBot) => {
    if (enlistedBots.includes(selectedBot)) {
      return;
    }
    setEnlistedBots([...enlistedBots, selectedBot]);
  };

  const deleteBot = (botId) => {
    fetch(`${url}/${botId}`, { method: "DELETE" })
      .then((response) => {
        if (response.ok) {
          setBots(bots.filter((bot) => bot.id !== botId));
        } else {
          throw new Error("Failed to delete bot");
        }
      })
      .catch((error) => console.log(error));
  };

  const releaseBot = (releasedBot) => {
    setEnlistedBots(
      enlistedBots.filter((enlistedBot) => enlistedBot !== releasedBot)
    );
  };

  // sorting
  //Get bots of the same class
  function getBotsOfSameClass(bot) {
    return enlistedBots.find(
      (enlistedBot) => enlistedBot.bot_class === bot.bot_class
    );
  }

  return (
    <div>
      <YourBotArmy
        enlistedBots={enlistedBots}
        deleteBot={deleteBot}
        releaseBot={releaseBot}
      />
      <BotCollection
        bots={bots}
        enlistBot={enlistBot}
        deleteBot={deleteBot}
        enlistedBots={enlistedBots}
        releaseBot={releaseBot}
      />
    </div>
  );
}

export default BotsPage;
