import React from "react";
import BotCard from "./BotCard";

function YourBotArmy({ enlistedBots, deleteBot, releaseBot }) {
  const handleReleaseClick = (bot) => {
    if (!enlistedBots.includes(bot)) {
      // Bot is not enlisted, do not release
      return;
    }
    releaseBot(bot);
  };

  return (
    <div className="ui segment inverted olive bot-army">
      <div className="ui five column grid">
        <div className="row">
          {enlistedBots.map((bot) => (
            <BotCard
              key={bot.id}
              bot={bot}
              enlisted={true}
              deleteBot={deleteBot}
              releaseBot={handleReleaseClick}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default YourBotArmy;
