import React from "react";
import BotCard from "./BotCard";

function BotCollection({ bots, enlistedBots, enlistBot }) {
  const handleEnlistClick = (bot) => {
    if (enlistedBots.includes(bot)) {
      // Bot is already enlisted, do not enlist again
      return;
    }
    enlistBot(bot);
  };

  return (
    <div className="ui segment inverted blue bot-collection">
      <div className="ui five column grid">
        <div className="row">
          {bots.map((bot) => (
            <BotCard
              key={bot.id}
              bot={bot}
              enlisted={enlistedBots.includes(bot)}
              enlistBot={handleEnlistClick}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default BotCollection;
