import React, { useState } from "react";

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};

function BotCard({ bot, enlistBot, releaseBot }) {
  const [isEnlisted, setIsEnlisted] = useState(false);

  const handleEnlist = () => {
    if (!isEnlisted) {
      setIsEnlisted(true);
      enlistBot(bot);
    }
  };

  const handleRelease = () => {
    if (isEnlisted) {
      setIsEnlisted(false);
      releaseBot(bot);
    }
  };

  return (
    <div className="ui column">
      <div className={`ui card ${isEnlisted ? "enlisted" : ""}`} key={bot.id}>
        <div className="image">
          <img alt="oh no!" src={bot.avatar_url} />
        </div>
        <div className="content">
          <div className="header">
            {bot.name}
            <i className={botTypeClasses[bot.bot_class]} />
          </div>
          <div className="meta text-wrap">
            <small>{bot.catchphrase}</small>
          </div>
        </div>
        <div className="extra content">
          <span>
            <i className="icon heartbeat" />
            {bot.health}
          </span>
          <span>
            <i className="icon lightning" />
            {bot.damage}
          </span>
          <span>
            <i className="icon shield" />
            {bot.armor}
          </span>
          <span>
            <div className="ui center aligned segment basic">
              {!isEnlisted && (
                <button className="ui mini green button" onClick={handleEnlist}>
                  Enlist
                </button>
              )}
              {isEnlisted && (
                <button className="ui mini red button" onClick={handleRelease}>
                  Release
                </button>
              )}
            </div>
          </span>
        </div>
      </div>
    </div>
  );
}

export default BotCard;
