import React, { useState, useEffect, useRef } from "react";
import YourBotArmy from "./YourBotArmy";
import BotCollection from "./BotCollection";
import BotCard from "./BotCard";
import BotSpecs from "./BotSpecs";
import SortPage from "./SortPage";

// Check if a bot is already listed
function alreadyListedBot(bot, botsListed) {
  return Boolean(botsListed.find((botListed) => botListed.id === bot.id));
}

// Get bots of the same class
function getBotsOfSameClass(bot, botsListed) {
  return botsListed.find((botListed) => botListed.bot_class === bot.bot_class);
}

function BotsPage() {
  const botsUrl = "http://localhost:8002/bots";
  const [bots, setBots] = useState([]);
  const [botsListed, setBotsListed] = useState([]);
  const [showBotSpecs, setShowBotSpecs] = useState(null);
  const [filteredBots, setFilteredBots] = useState([]);
  const [showSortBar, setShowSortBar] = useState(true); // Initially visible
  const sortStrategy = useRef({ health: 1, damage: 1, armor: 1 });

  // Fetch Bots
  useEffect(() => {
    fetch(`${botsUrl}`)
      .then((response) => response.json())
      .then((data) => {
        setBots(data);
        setFilteredBots(data);
      });
  }, []);

  // Delete bot from server


  // Handler for Bot Actions
  function handleBotActionClick(bot, action) {
    switch (action) {
      case "release-bot":
        deleteBot(bot);
        break;

      case "enlist-bot":
        const listedBotsOfSameClass = getBotsOfSameClass(bot, botsListed);
        if (!listedBotsOfSameClass) {
          setBotsListed([...botsListed, bot]);
          setFilteredBots(
            filteredBots.filter((currentBot) => currentBot.id !== bot.id)
          );
        } else {
          alert(
            `${listedBotsOfSameClass.name} has already been enlisted for the ${bot.bot_class} role`
          );
        }
        break;

      case "delist-bot":
        setBotsListed(
          botsListed.filter((currentBot) => currentBot.id !== bot.id)
        );
        setFilteredBots([...filteredBots, bot]);
        break;

      case "show-all-bots":
        setShowBotSpecs(null);
        setShowSortBar(true);
        break;

      case "show-bot-specs":
        setShowBotSpecs(bot);
        setShowSortBar(false);
        break;

      default:
        // Handle any unhandled actions
        break;
    }
  }

  // Sorting Functionality
  function sortBots(data, sortBy) {
    data.sort((a, b) => {
      if (a[sortBy] > b[sortBy]) {
        return sortStrategy.current[sortBy] * 1;
      } else if (a[sortBy] < b[sortBy]) {
        return sortStrategy.current[sortBy] * -1;
      } else {
        return 0;
      }
    });

    return data;
  }

  function updateSortStrategy(sortBy) {
    sortStrategy.current[sortBy] *= -1; // If it was ascending, make it descending and vice versa
  }

  function handleSortAction(sortBy) {
    updateSortStrategy(sortBy);
    setFilteredBots(sortBots([...filteredBots], sortBy));
  }

  function renderBotCards(botsArray) {
    return botsArray.map((bot) => (
      <BotCard
        key={bot.id}
        bot={bot}
        handleBotActionClick={handleBotActionClick}
      />
    ));
  }

  return (
    <div>
      <YourBotArmy
        botsListed={renderBotCards(botsListed)}
        handleBotActionClick={handleBotActionClick}
      />
      {showSortBar && <SortPage handleSortAction={handleSortAction} />}
      {showBotSpecs ? (
        <BotSpecs
          bot={showBotSpecs}
          handleBotActionClick={handleBotActionClick}
          alreadyListedBot={alreadyListedBot(showBotSpecs, botsListed)}
        />
      ) : (
        <BotCollection filteredBots={renderBotCards(filteredBots)} />
      )}
    </div>
  );
}

export default BotsPage;
